protected float shapeAreaWidth(){
    if (this.fixed)
        return this.font.em*this.getCols();
    else {
        Shape shape = this.shape();
        if (null != shape){
            Rectangle2D bounds = shape.getBounds2D();
            return (float)(bounds.getX()+bounds.getWidth());
        }
        else {
            return this.font.em*this.getCols();
        }
    }
}
